import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Spinner from './Spinner';



export class News extends Component {
   articles = [
    {
      "source": { "id": "espn-cric-info", "name": "ESPN Cric Info" },
      "author": null,
      "title": "PCB hands Umar Akmal three-year ban from all cricket | ESPNcricinfo.com",
      "description": "Penalty after the batsman pleaded guilty to not reporting corrupt approaches | ESPNcricinfo.com",
      "url": "http://www.espncricinfo.com/story/_/id/29103103/pcb-hands-umar-akmal-three-year-ban-all-cricket",
      "urlToImage": "https://a4.espncdn.com/combiner/i?img=%2Fi%2Fcricket%2Fcricinfo%2F1099495_800x450.jpg",
      "publishedAt": "2020-04-27T11:41:47Z",
      "content": "Umar Akmal's troubled cricket career has hit its biggest roadblock yet, with the PCB handing him a ban from all representative cricket for three years after he pleaded guilty of failing to report det… [+1506 chars]"
    }
  ]
  constructor(){
    super();
    this.state ={
      articles: this.articles,
      loading : false,
      page: 1
    }
  }

  async componentDidMount(){
    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&apiKey=15e2c91b28524e4aa87f35cb8a0fdb47&pageSize=${this.props.pageSize}`
    this.setState({loading: true});
    let data = await fetch(url);
    let parsdData = await data.json()
    this.setState({
      articles : parsdData.articles,
       totalResults: parsdData.totalResults,
      loading : false
      })
  }
  handlePrevClick = async ()=>{
    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&apiKey=15e2c91b28524e4aa87f35cb8a0fdb47&page=${this.state.page - 1}&pageSize=${this.props.pageSize}`
    this.setState({loading: true});
    let data = await fetch(url);
    let parsdData = await data.json()
    this.setState({
      articles : parsdData.articles,
      page : this.state.page - 1 ,
      loading : false
    })
  }
  handleNextClick = async ()=>{
    if(!(this.state.page + 1 > Math.ceil(this.state.totalResults/this.props.pageSize))){
    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&apiKey=15e2c91b28524e4aa87f35cb8a0fdb47&page=${this.state.page + 1}&pageSize=${this.props.pageSize}`
    this.setState({loading: true});
    let data = await fetch(url);
    let parsdData = await data.json()
    console.log(parsdData)
    this.setState({
      articles : parsdData.articles,
      page : this.state.page + 1 ,
      loading : false
    })
  }
  }


  render() {
    return (
      <div>
      <div className='container my-3'>
        <h1 className='text-center'>News Flash - Top News </h1>
        {this.state.loading && <Spinner/>}
        <div className='row'>
          {!this.state.loading && this.state.articles.map((element)=>{
            return <div className='col-md-4' key={element.url}>
            <NewsItem title={element.title?element.title.slice(0, 45):""} discription={element.description?element.description.slice(0, 88):""} imgurl={element.urlToImage?element.urlToImage:"https://a4.espncdn.com/combiner/i?img=%2Fi%2Fcricket%2Fcricinfo%2F1219926_1296x729.jpg"} newsUrl={element.url}/>  
         </div>
          })}
        </div>     
      </div>
      <div className='container d-flex justify-content-between'>
      <button disabled={this.state.page<=1} type="button" className="btn btn-dark" onClick={this.handlePrevClick}>&larr; Prev</button>
      <button disabled={this.state.page + 1 > Math.ceil(this.state.totalResults/this.props.pageSize)} type="button" className="btn btn-dark" onClick={this.handleNextClick}>Next &rarr;</button>
      </div>
      </div>
    )
  }
}

export default News
