import React, { Component } from "react";

export class NewsItem extends Component {
  render() {
    let { title, discription, imgurl, newsUrl, author, date, source } =
      this.props;
    return (
      <div>
        <span
          class="position-absolute top-0 translate-middle badge 
               bg-primary"
          style={{ left: "90%", zIndex: "3" }}
        >
          {source}
        </span>
        <div
          className="card hover-shadow bg-image hover-zoom "
          style={{ height: "500px" }}
        >
          <img src={imgurl} className="card-img-top" alt="..." />
          <div className="card-body ">
            <h5 className="card-title">{title}...</h5>
            <p className="card-text">{discription}...</p>
            <p className="card-text">
              <small className="text-body-secondary">
                By {!author ? "Unkown" : author} on {Date(date).toString()}
              </small>
            </p>
            <a
              href={newsUrl}
              target="_blank"
              rel="noreferrer"
              className="btn btn-sm btn-dark"
            >
              Read more
            </a>
          </div>
        </div>
      </div>
    );
  }
}

export default NewsItem;
