import React, { Component } from 'react'

export class NewsItem extends Component {
  render() {
    let {title, discription, imgurl, newsUrl, author, date} = this.props
    return (
      
      <div>
      <div className="card">
         <img src={imgurl} className="card-img-top" alt="..."/>
    <div className="card-body">
       <h5 className="card-title">{title}...</h5>
       <p className="card-text">{discription}...</p>
       <p className="card-text"><small className="text-body-secondary">By {!author?"Unkown":author} on {date}</small></p>
       <a href={newsUrl} target="#" className="btn btn-sm btn-dark">Read more</a>
    </div>
     </div>
      </div>
      
    )
  }
}

export default NewsItem
